mapper.insert:
insert into bl_log (
    log_url, log_params, log_status, log_message, log_method, log_time, log_result, log_ip
) values (
    #{logUrl},#{logParams},#{logStatus},#{logMessage},#{logMethod},#{logTime},
    #{logResult},#{logIp}
)

mapper.delete:
delete from bl_log
where log_id in

    #{id}

mapper.select:
select
log_id, log_url, log_params, log_status,
log_message, log_method, log_time, log_result, log_ip, created_time
from bl_log


        and log_url = #{params.logUrl }


    and log_status = #{params.logStatus }


    and log_method = #{params.logMethod }


    and log_ip = #{params.logIp }



    order by ${sortColumn} ${sortMethod}

limit #{index}, #{pageSize}

select
count(*)
from bl_log


        and log_url = #{params.logUrl }


    and log_status = #{params.logStatus }


    and log_method = #{params.logMethod }


    and log_ip = #{params.logIp }
